import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'DirectivesAssignment';
  public myLinks: {name: string, value: string}[] = [
    {name: 'Component 1', value: 'comp1'},
    {name: 'Component 2', value: 'comp2'},
    {name: 'Component 3', value: 'comp3'},
  ];

  public currDisplay: string = '';
  public showMenu: boolean = false;


constructor() {
  this.currDisplay = '';
  this.showMenu = false;
}

switchContent(content: string) {
  this.currDisplay = content;
}

changeMenuDisplay() {
  this.showMenu = !this.showMenu;
}
}
