import { Component } from '@angular/core';

@Component({
  selector: 'app-playground',
  templateUrl: './playground.component.html',
  styleUrls: ['./playground.component.css']
})
export class PlaygroundComponent {
  public lightsOn = false;

  turnOnLights() {
      this.lightsOn = true;
  }

  turnOffLights() {
      this.lightsOn = false;
  }

}
