import { Component } from '@angular/core';
import { Person } from './personal';

@Component({
  selector: 'app-personal-form',
  templateUrl: './personal-form.component.html',
  styleUrls: ['./personal-form.component.css']
})
export class PersonalFormComponent {

  pronouns = ['He/Him', 'She/Her',
    'They/Them', 'Other'];

  residents = ['Yes', 'No'];

  halls = ['Appalachian Heights', 'Belk Hall', 'Cannon Hall',
      'Cone Hall', 'Dogwood Hall', 'Doughton Hall', 'Elkstone Hall',
      'Frank Hall', 'Laurel Creek Hall', 'Living Learning Center (LLC)',
      'Mountain Laurel Hall', 'Mountaineer Hall', 'Newland Hall',
      'New River Hall', 'Raven Rocks', 'Sleep Inn', 'Summit Hall',
      'Thunder Hill', 'White Hall'];

  model = new Person(0, '', '', '', '', '');
  submitted = false;
  showAdditional1 = false;
  showAdditional2 = false;

  onSubmit() {
    this.submitted = true;
  }

  newHero() {
    this.model = new Person(42, '', '', '', '');
  }

  showAdditionalSection1() {
    if (this.model.pronoun === 'pronoun[3]') {
      this.showAdditional1 = true;
    }
    else {
      this.showAdditional1 = false;
    }
  }

  showAdditionalSection2() {
    if (this.model.resident === 'resident[1]') {
      this.showAdditional2 = true;
    }
    else {
      this.showAdditional2 = false;
    }
  }
}
<div class="container">
  <div [hidden]="submitted">
    <h1>Personal Information</h1>
    <form (ngSubmit)="onSubmit()" #heroForm="ngForm">

      /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

      <div class="form-group">
        <label for="name">Name:</label>
        <input type="text" class="form-control" id="name" required [(ngModel)]="model.name" name="name" #name="ngModel">
        <div [hidden]="name.valid || name.pristine" class="alert alert-danger">
          Your name is required
        </div>
      </div>

      /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

      <div class="form-group">
        <label for="email">Email: </label>
        <input type="text" class="form-control" id="email" required [(ngModel)]="model.email" name="email"
          #name="ngModel">
        <div [hidden]="name.valid || name.pristine" class="alert alert-danger">
          Your email is required
        </div>
      </div>

      /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

      <div class="form-group">
        <label for="pronoun">Pronouns: </label>
        <select class="form-control" id="pronoun" required [(ngModel)]="model.pronoun" name="pronoun" #pronoun="ngModel"
          (change)="showAdditionalSection1()">
          <option *ngFor="let pow of pronouns" [value]="pow">{{pow}}</option>
        </select>
        <div [hidden]="pronoun.valid || pronoun.pristine" class="alert alert-danger">
          Your pronouns are required
        </div>
      </div>

      <div *ngIf="model.pronoun === 'Other'">
        <div class="form-group">
          <label for="other">Please enter your pronouns: </label>
          <input type="text" class="form-control" id="other" required [(ngModel)]="model.other" name="other"
            #name="ngModel">
        </div>
      </div>

      /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

      <div class="form-group">
        <label for="resident">Do you reside on-campus? </label>
        <select class="form-control" id="resident" required [(ngModel)]="model.resident" name="resident" #resident="ngModel"
          (change)="showAdditionalSection2()">
          <option *ngFor="let live of residents" [value]="live">{{live}}</option>
        </select>
        <div [hidden]="resident.valid || resident.pristine" class="alert alert-danger">
          Your response is required
        </div>
      </div>

      <div *ngIf="model.resident === 'Yes'">
        <div class="form-group">
          <label for="all">Select your residence hall: </label>
          <select class="form-control" id="resident" required [(ngModel)]="model.resident" name="resident" #resident="ngModel">
            <option *ngFor="let house of halls" [value]="house">{{house}}</option>
          </select>
        </div>
      </div>

      /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

      <button type="submit" class="btn btn-success" [disabled]="!heroForm.form.valid">Submit Form</button>
      &nbsp;&nbsp;
      <button type="button" class="btn btn-default" (click)="newHero(); heroForm.reset()">Clear</button>

      <!-- NOT SHOWN IN DOCS -->
      <!-- <div>
        <hr>
        Name via form.controls = {{showFormControls(heroForm)}}
      </div> -->
      <!-- - -->
    </form>
  </div>

  <div [hidden]="!submitted">
    <h2>Thank you for your submission.</h2>
    <h2>Here is a summary of what you have submitted</h2>
    <div class="row">
      <div class="col-xs-3">Name: </div>
      <div class="col-xs-9">{{ model.name }}</div>
    </div>
    <div class="row">
      <div class="col-xs-3">Email: </div>
      <div class="col-xs-9">{{ model.email }}</div>
    </div>
    <div class="row">
      <div class="col-xs-3">Pronouns: </div>
      <div class="col-xs-9">{{ model.pronoun }}</div>
    </div>
    <br>
    <button type="button" class="btn btn-primary" (click)="submitted=false">Edit Submission</button>
  </div>
</div>

<style>
  .no-style .ng-valid {
    border-left: 1px solid #CCC
  }

  .no-style .ng-invalid {
    border-left: 1px solid #CCC
  }
</style>
